import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import Header from './Header';

function Readblog(props) {
    const { title } = useParams()
    const id = title
    const [data, updateData] = useState({ name: "", description: "" })

    const course = props.course
    const blogs = props.blogs
    console.log(blogs);
    useEffect(() => {
        window.scrollTo(0,0)
        course.forEach(element => {
            if (element.$id == id) {
                updateData(element)
                return
            }
        });
        blogs.forEach((element)=>{
            if ( element.$id == id) {
                updateData(element)
                return
            }
        })
    }, [course,blogs])
    // console.log(data);
    return (
        <>
            <Header />
            <div className="container px-lg-5 px-md-3 px-1">
                <h1 className="text-start mt-4" style={{ fontSize: "45px", fontWeight: "600", textTransform: "capitalize" }} data-aos="fade-right">{data.name}</h1>

                <img src="https://images.unsplash.com/photo-1497366811353-6870744d04b2?ixlib=rb-4.0.3&ixtitle=M3wxMjA3fDB8MHxzZWFyY2h8M3x8b2ZmaWNlfGVufDB8fDB8fHww&auto=format&fit=crop&w=500&q=60" alt="" className="w-100 d-block mx-auto" data-aos="fade-down" />

                <h3 className="text-start mt-5" style={{ fontSize: "28px", fontWeight: 700 }} data-aos="fade-left">What is lorem ipsum ?</h3>
                <h5 className='mb-5' style={{ textAlign: "justify", fontSize: "25px", fontFamily: "source sans pro", lineHeight: "30px" }} data-aos="fade-right">{data.description}</h5>
            </div>
            <div className="container-flutitle d-flex flex-md-row flex-column justify-content-center pt-4 pb-3 text-light align-items-center" data-aos="fade-down" style={{ backgroundColor: "#26475D" }}>
                <h2>Boost Your Career with <span style={{ color: "#FF900E" }}>INTERNACC</span> </h2>
                <button className="btn btn-light px-5 mb-2 ms-4 d-block align-self-center">Apply Now</button>
            </div>

        </>
    )
}

export default Readblog
