import React, { useEffect, useState } from 'react'
import Card from './Card'
import Header from './Header'


function Course(props) {
  const [subject, updateSubjects] = useState([])
  const [university, updateUniversity] = useState([])
  const [courses, updateCourses] = useState([])
  const { course } = props
  console.log(course);


  useEffect(() => {

    let tempSubjList = []
    let tempUnivList = []
    course.forEach(element => {
      tempSubjList.push(element.subject)
      tempUnivList.push(element.university)
    });
    console.log(tempSubjList);
    console.log(tempUnivList);

    function removeDuplicates(tempSubjList) {
      return [...new Set(tempSubjList)];
    }
    let filteredSubjList = removeDuplicates(tempSubjList)
    let filteredUnivList = removeDuplicates(tempUnivList)

    updateSubjects(filteredSubjList)
    updateUniversity(filteredUnivList)
    updateCourses(course)
  }, [course])

  const applyFilters = (e) => {
    let filteredCourses = []
    let selectedSubject = document.getElementById("subject").value
    let selectedAge = document.getElementById("age").value
    let selectedUniversity = document.getElementById("university").value
    course.map((element) => {


      if ((selectedSubject != "all" ? element.subject == selectedSubject : true) && (selectedAge != "all" ? element.aggrp == selectedAge : true) && (selectedUniversity != "all" ? element.university == selectedUniversity : true)) {
        filteredCourses.push(element)
      }
    })
    updateCourses(filteredCourses)
  }
  return (
    <>
      <Header />
      <h1 className="text-start ms-2 mt-4" style={{ fontSize: "45px", fontWeight: 700, fontFamily: "constantia" }}>Explore Course</h1>

      <div className="container-fluid bg-secondary mt-4" style={{ minHeight: "60vh " }}>

      </div>
      <div className="container-fluid px-md-5 px-1 mt-3 mb-4">
        <h1 className="text-start" style={{ fontSize: "27px", fontWeight: "700" }}>Filters :</h1>

        <div className="container-fluid ">
          <div className="row">
            <div className="col-4">
              <div className="form-group">
                <label htmlFor="" className="form-label">Subject : </label>
                <select name="" id="subject" className="form-select" onChange={applyFilters}>
                  <option value={"all"}>{"All"}</option>
                  {
                    subject.map((element, index) => {
                      return <option value={element} key={index}>{element}</option>
                    })
                  }

                  {/* <option value="">Busines and Enterpreneurship</option>
                  <option value="">Computer Science</option>
                  <option value="">More</option> */}
                </select>
              </div>
            </div>
            <div className="col-4">
              <div className="form-group">
                <label htmlFor="" className="form-label">Age : </label>
                <select name="" id="age" className="form-select" onChange={applyFilters}>
                  <option value={"all"}>{"All"}</option>
                  <option value={0}>9 - 12</option>
                  <option value={1}>13 - 16</option>
                  <option value={2}>17 - 20</option>
                  <option value={3}>21 - 24</option>
                </select>
              </div>
            </div>
            <div className="col-4">
              <div className="form-group">
                <label htmlFor="" className="form-label">University : </label>
                <select name="" id="university" className="form-select" onChange={applyFilters}>
                  <option value={"all"}>{"All"}</option>
                  {
                    university.map((element, index) => {
                      return <option value={element} key={index}>{element}</option>
                    })
                  }
                </select>
              </div>
            </div>
          </div>
        </div>

        <div className="row row-cols-xl-3 row-cols-lg-2 pt-4 row-cols-1 mt-2">
          {courses.map((element, index) => {
            return <div className="col" key={index}>
              <Card img={"https://images.unsplash.com/photo-1616400619175-5beda3a17896?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8MTB8fHN0dWR5fGVufDB8fDB8fHww&auto=format&fit=crop&w=500&q=60"} title={element.name} description={"Lorem ipsum dolor sit amet consectetur adipisicing elit. Inventore ut iste voluptatum facere molestiae tempora earum quod culpa voluptatibus cum. Nemo in necessitatibus, nisi deserunt quos asperiores ratione?"} />
            </div>
          })}
        </div>
      </div>



    </>
  )
}

export default Course
