import './App.css';
import React from 'react';
import { useState } from 'react';
import Header from './components/Header';
import Footer from './components/Footer';
import Home from './components/Home';
import Aboutus from './components/Aboutus';
import Contact from './components/Contact';
import Course from './components/Course';
import Blog from './components/Blog';
import Accomodation from './components/Accomodation'
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link
} from 'react-router-dom';
import Readblog from './components/Readblog';

import { Client,Databases,ID, Query, Account } from 'appwrite';

const client = new Client();
client
    .setEndpoint('https://cloud.appwrite.io/v1')
    .setProject('64b92b85162ae2a4bba8');

// const account = new Account(client);
// const authPromise = account.createEmailSession('ujjvaljoshi45@gmail.com', 'UP#appwrite@17');


// authPromise.then(function (response) {
//     console.log(response); // Success
// }, function (error) {
//     console.log(error); // Failure
// });
const database = new Databases(client);
const promise = database.listDocuments('64b92e033b36ffa5f41d', '64b92e0896b7047d3196');
/*
  Schema
  name required
  aggrp required
  university required
  subject required
  description required
  photo required (working on getting it from database)
*/
function getData() {
    promise.then(function (response) {
      console.log(response);
    }, function (error) {
      console.log(error);
    });
}
function App() {
  const [course,updateCourse] = useState()
  getData();
  base64ToStringConverter();
  return (
    <>
      <Router>
        <Header />
        <Routes>
          <Route exact path="/" element={<Home />}></Route>
          <Route exact path="/blog" element={<Blog />}></Route>
          <Route exact path="/blog/:title" element={<Readblog />}></Route>
          <Route exact path="/course" element={<Course />}></Route>
          <Route exact path="/accomodation" element={<Accomodation />}></Route>
          <Route exact path="/about" element={<Aboutus />}></Route>
          <Route exact path="/contact" element={<Contact />}></Route>
        </Routes>

      <Footer />
      </Router>
    </>
  );
}

function base64ToStringConverter() {
  let target = "SGVsbG8gV29ybGQ=";
  let str = atob(target);
  console.log(str);
  let base64 = btoa(str);
  console.log(base64);
}

export default App;
