import React from 'react'
import Adminnav from './Adminnav'
import { Link } from 'react-router-dom'
import { useState , useEffect } from 'react'
import { Client, Databases, ID, Query, Storage } from 'appwrite';

function addImage() {
    // 64be822fd5ed08d62229
